// Santa is trying to deliver presents in a large apartment building, but he can't find the right floor - the directions he got are a little confusing. He starts on the ground floor (floor 0) and then follows the instructions one character at a time.
//
// An opening parenthesis, (, means he should go up one floor, and a closing parenthesis, ), means he should go down one floor.

// To what floor do the instructions take Santa?

// Now, given the same instructions, find the position of the first character that causes him to enter the basement (floor -1).
// The first character in the instructions has position 1, the second character has position 2, and so on.

// What is the position of the character that causes Santa to first enter the basement?

#include <iostream>
#include <vector>
#include <string>
int main() {
    std::string stairs {};
    std::cout << "Give Santa the instructions for the delivery: \n";
    std::cin >> stairs;
    int level {};
    std::vector<int> basement {};
    
    for (unsigned int i{0}; i < stairs.length(); i++) {
        std::cout << stairs.at(i) << " - ";
        if (stairs.at(i) == '(') {
            std::cout << "Go up one floor.\n";
            level = level + 1;
        } else if (stairs.at(i) == ')') {
            std::cout << "Go down one floor.\n";
            level = level - 1;
        }
        if (level == -1) {
            basement.push_back(i + 1);
        }
    }
    
    std::cout << "\n";
    std::cout << "Santa is currently at floor " << level << "\n";
    std::cout << "First character that causes Santa to enter the basement is: " << basement.at(0) << "\n";
    std::cout << "\n";
    
    return 0;
}

